vals.css:
.modal1-root {
  border-radius: {{radius}}px;
  box-shadow: 0 {{shadow}}px {{shadow*3}}px rgba(0, 0, 0, 0.5);
  opacity: 0;
  transform: scale(1.1);
  transition: {{_duration}}ms all ease-in;
}

.modal1-root.is-active {
  opacity: 1;
  transform: scale(1);
}

.modal1-root.is-hiding {
  opacity: 0;
  transform: scale(0.9);
}

.modal1-panel {
  padding: 15px;
  border-radius: {{radius}}px;
  background-color: {{bgcolor}};
}

.modal1-divider {
	margin-top: 10px;
  height: 1px;
  background-color: rgba(0, 0, 0, 0.05);
  margin-bottom: 10px;
}

.modal1-close {
  cursor: pointer;
  background-color: {{closecolor==='white'?'white':'black'}};
  border: 2px solid {{closecolor==='white'?'black':'white'}};
  border-radius: 100px;
  position: absolute;
  width: 30px;
  height: 30px;
  padding: 0;
  top: -10px;
  right: -10px;
  transition: transform 200ms;
}

.modal1-close-h, .modal1-close-v {
  background-color: {{closecolor==='white'?'black':'white'}};
  display: block;
  width: 16px;
  height: 2px;
  position: absolute;
  left: 50%;
  top: 50%;
}

.modal1-close-h {
	transform: translate(-50%, -50%) rotate(135deg);
}

.modal1-close-v {
	transform: translate(-50%, -50%) rotate(45deg);
}

.modal1-close:hover {
  transform: scale(1.1);
}

.menu1-root {
  position: relative;
  background-color: {{bgcolor}};
  outline: none;
  opacity: 0;
  transform: translateX({{_side==='left'?'-':''}}100%);
  transition: {{_duration}}ms transform, {{_duration}}ms opacity;
  box-shadow: {{shadow}}px 0 {{shadow*6}}px rgba(0, 0, 0, 0.1);
}

.menu1-root.is-active {
  opacity: 1;
  transform: translateX(0);
}

.menu1-root.is-hiding {
  opacity: 0;
  transform: translateX({{_side==='left'?'-':''}}100%);
}

.menu1-header {
  padding: {{vpaddings}}px {{hpaddings}}px;
  border-bottom: 1px solid {{bgcolor.l_inv(0.1)}};
  color: {{bgcolor.l_inv(1)}};
  min-height: 16px;
}

.menu1-body {
  position: relative;
  padding: {{vpaddings}}px {{hpaddings}}px;
}

.menu1-title {
  margin-right: 20px;
  text-align: left;
}

.menu1-close {
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
  padding: 0;
  cursor: pointer;
  background: transparent;
  border: 0;
  float: right;
  font-size: 24px;
  line-height: 1;
  opacity: 0.5;
  margin-top: -4px;
}

.menu1-close:hover {
  text-decoration: none;
  cursor: pointer;
  opacity: 0.75;
}

.popover1-root {
  perspective: 1000px;
}

.popover1-container {
  position: relative;
  border-radius: {{radius}}px;
  background-color: {{bgcolor}};
  border: 1px solid {{bgcolor.l_dec(0.05)}};
  box-shadow: 0 {{shadow}}px {{shadow*6}}px rgba(0, 0, 0, 0.1);
  background-clip: padding-box;
  outline: none;
  padding: {{vpaddings}}px {{hpaddings}}px;
  margin-top: {{arrow/1.5}}px;
  opacity: 0;
  transform-origin: 50% -50px;
  transform: rotateX(-15deg);
  transition: {{_duration}}ms transform ease-out, {{_duration}}ms opacity ease-out;
}

.popover1-root.is-active .popover1-container {
  opacity: 1;
  transform: translateY(0);
}

.popover1-root.is-hiding .popover1-container {
  opacity: 0;
  transform: rotateX(-15deg);
}

.popover1-arrow {
  text-align: center;
  position: absolute;
  line-height: 0;
  top: 0;
  left: 0;
  right: 0;
}

.popover1-arrow div {
  width: {{arrow}}px;
  height: {{arrow}}px;
  background-color: {{bgcolor}};
  border: 1px solid {{bgcolor.l_dec(0.05)}};
  transform: translateY(-50%) rotate(45deg);
  margin-top: -1px;
  position: relative;
  display: inline-block;
  border-width: 1px 0 0 1px;
  vertical-align: top;
}

.popover1-body {
  position: relative;
  padding: 20px;
}

.popover1-title {
  margin-right: 20px;
  text-align: left;
}

.popover1-close {
  font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
  padding: 0;
  cursor: pointer;
  background: transparent;
  border: 0;
  -webkit-appearance: none;
  float: right;
